#!/usr/bin/env bash\nset -euo pipefail\n\n# update-version.sh\n# Script to update the version in pyproject.toml\n\nif [[ $# -ne 1 ]]; then\n  echo \"Usage: $0 <version>\" >&2\n  exit 1\nfi\n\nVERSION=\"$1\"\n\n# Remove 'v' prefix if present\nVERSION_NO_V=${VERSION#v}\n\n# Update the version in pyproject.toml\nsed -i \"s/version = \\\".*\\\"/version = \\\"$VERSION_NO_V\\\"/g\" goal-kit/pyproject.toml\n\necho \"Updated version in goal-kit/pyproject.toml to $VERSION_NO_V\"\n\n# If we're in a git repository, commit the change\nif git status >/dev/null 2>&1; then\n  git config --local user.email \"action@github.com\"\n  git config --local user.name \"GitHub Action\"\n  git add goal-kit/pyproject.toml\n  git commit -m \"chore: update version to $VERSION\"\n  echo \"Committed version update\"\nfi